import React, {Component} from "react";

class MemeGenerator extends Component {
    constructor () {
        super();
        this.state = {
            topText: "",
            bottomText: "",
            randomImage: "http://i.imgflip.com/1bij.jpg",
            allMemeImgs: []
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        })
    }
    componentDidMount () {
        fetch("https://api.imgflip.com/get_memes")
            .then(response => response.json())
            .then(response => {
                const {memes} = response.data
                this.setState({ allMemeImgs: memes })
            })
    }
    handleSubmit(event) {
        this.setState(prevState => {
            return {
                allMemeImgs: prevState[Math.floor(Math.random() * prevState.length)]
            }
        })
    }
    render() {
        return (
            <div className="meme">
                <input 
                    type="text" 
                    name="topText" 
                    placeholder="top text" 
                    value={this.state.topText} 
                    onChange={this.handleChange}
                />
                <input type="text" 
                    name="bottomText" 
                    placeholder="bottom text" 
                    value={this.state.bottomText} 
                    onChange={this.handleChange}
                />
                <button onClick={this.handleClick}>Change image</button>
                <img src={this.state.allMemeImgs.url} alt="problem?"></img>
                <h1 className="top">{this.state.topText}</h1>
                <h1 className="bottom">{this.state.bottomText}</h1>
            </div>
        )
    }
}

export default MemeGenerator;
